avgxy = sum(particleLocations,1)./500
stdxy = particleLocations - avgxy;
stdxy = sqrt(sum(stdxy .* stdxy)/499);
stdx = [avgxy(1) - stdxy(1),avgxy(1) + stdxy(1)]
stdy = [avgxy(2) - stdxy(2),avgxy(2) + stdxy(2)]
plot(stdx,[avgxy(2),avgxy(2)],'-|')
hold on
plot([avgxy(1),avgxy(1)],stdy,'-|')
hold on

covxy = cov(particleLocations(:,1),particleLocations(:,2)) .* 1000
covxy = [avgxy(1)+covxy(1,1),avgxy(2)+covxy(1,2)]
plot([avgxy(1),covxy(1)],[avgxy(2),covxy(2)],'->');
hold on

plot(particleLocations(:,1),particleLocations(:,2),'.r')
hold on
plot(avgxy(1),avgxy(2),'x','MarkerSize',15)
hold on

